import React from 'react';
import Navbar from './components/Navbar';
import Section from './components/Section';
import Latex from 'react-latex-next';

function App() {
  const sections = [
    {
      id: 'edge-detector',
      title: 'Edge Detection',
      content: [
        { type: 'paragraph', text: 'Inspired by the partial derivative, the finite difference filters represent the respective partial derivatives in the x and y directions. These filters can be convolved with an input image to obtain edges.' },

        // Math formula for partial derivative
        { type: 'math', text: '$\\frac{\\partial f}{\\partial x}$ and $\\frac{\\partial f}{\\partial y}$ represent the partial derivatives in the x and y directions respectively.' },

        // Show the corresponding filters D_x and D_y
        { type: 'math', text: '$D_x = \\begin{bmatrix} 1 & -1 \\end{bmatrix}, \\quad D_y = \\begin{bmatrix} 1 \\\\ -1 \\end{bmatrix}$' },

        { type: 'paragraph', text: 'Convolving an image with the $D_x$ filter will highlight vertical edges, while convolving with the $D_y$ filter will highlight horizontal edges.' },

        // Formula for gradient magnitude
        { type: 'math', text: 'The gradient magnitude can be computed as $\\sqrt{\\left( \\frac{\\partial f}{\\partial x} \\right)^2 + \\left( \\frac{\\partial f}{\\partial y} \\right)^2}$ element-wise.' },

        { type: 'paragraph', text: 'After obtaining the $D_x$, $D_y$, and gradient magnitude images, we can binarize them using an appropriate threshold to get a clearer edge image.' },

        { type: 'image', title: 'Sobel Filter', imageUrl: '/images/sobel.jpg' },
      ],
    },
    {
      id: 'image-sharpening',
      title: 'Image Sharpening',
      content: [
        { type: 'paragraph', text: 'Image sharpening enhances image details by amplifying high frequencies.' },
        { type: 'image', title: 'Laplacian Sharpening', imageUrl: '/images/laplacian.jpg' },
      ],
    },
    {
      id: 'hybrid-images',
      title: 'Hybrid Images',
      content: [
        { type: 'paragraph', text: 'Hybrid images combine low-frequency components of one image with high-frequency components of another.' },
        { type: 'image', title: 'Hybrid Technique', imageUrl: '/images/hybrid.jpg' },
      ],
    },
    {
      id: 'image-blending',
      title: 'Image Blending',
      content: [
        { type: 'paragraph', text: 'Image blending merges images using gradient or multi-scale techniques.' },
        { type: 'image', title: 'Blend Modes', imageUrl: '/images/blending.jpg' },
      ],
    },
  ];

  return (
    <div className="flex min-h-screen bg-gradient-to-r from-gray-900 via-gray-800 to-black text-gray-300">
      <Navbar />
      <div className="flex-1 p-8 ml-64">
        <h1 className="text-4xl font-bold text-center mb-12 text-white">Image Filters</h1>

        {sections.map((section) => (
          <Section
            key={section.id}
            id={section.id}
            title={section.title}
            content={section.content}
          />
        ))}
      </div>
    </div>
  );
}

export default App;
