import React from 'react';
import Navbar from './components/Navbar';
import Section from './components/Section';

function App() {
  const sections = [
    {
      id: 'edge-detector',
      title: 'Edge Detection',
      content: [
        { type: 'paragraph', text: 'Inspired by the partial derivative, the finite difference filters represent the respective partial derivatives in the x and y directions. These filters can be convolved with an input image to obtain edges.' },
        { type: 'math', text: '\\( \\frac{\\partial f}{\\partial x} = \\lim_{h \\to 0} \\frac{f(x+h) - f(x)}{h}, \\quad \\frac{\\partial f}{\\partial y} = \\lim_{h \\to 0} \\frac{f(y+h) - f(y)}{h} \\)' },
        { type: 'math', text: '\\( D_x = \\begin{bmatrix} 1 & -1 \\end{bmatrix}, \\quad D_y = \\begin{bmatrix} 1 \\\\ -1 \\end{bmatrix} \\)' },
        { type: 'paragraph', text: 'Convolving an image with the Dx filter will highlight vertical edges, while convolving with the Dy filter will highlight horizontal edges.' },
        { type: 'paragraph', text: 'The gradient magnitude can be computed element-wise from the images filtered by Dx and Dy.' },
        { type: 'math', text: '\\( \\text{Gradient Magnitude} = \\sqrt{\\left( \\frac{\\partial f}{\\partial x} \\right)^2 + \\left( \\frac{\\partial f}{\\partial y} \\right)^2} \\)' },
        { type: 'paragraph', text: 'After obtaining the Dx-filtered, Dy-filtered, and gradient magnitude images, we can binarize them using an appropriate threshold to get a clearer edge image.' },
        {
          type: 'image-grid',
          columns: 2,
          images: [
            { title: 'Sobel Filter', imageUrl: '/images/sobel.jpg' },
            { title: 'Prewitt Filter', imageUrl: '/images/prewitt.jpg' },
          ],
        },
      ],
    },
    // Other sections...
  ];

  return (
    <div className="flex min-h-screen bg-gradient-to-r from-gray-900 via-gray-800 to-black text-gray-300">
      <Navbar />
      <div className="flex-1 px-4 lg:px-8 py-8 ml-64 w-full"> {/* Adjusted padding and width */}
        <h1 className="text-4xl font-bold text-center mb-12 text-white">Image Filters</h1>

        {sections.map((section) => (
          <Section
            key={section.id}
            id={section.id}
            title={section.title}
            content={section.content}
          />
        ))}
      </div>
    </div>
  );
}

export default App;
