import React, { useState } from 'react';

function ImageCard({ title, imageUrl }) {
  const [isExpanded, setIsExpanded] = useState(false);

  // Handle image click to expand or close
  const toggleExpand = () => {
    setIsExpanded(!isExpanded);
  };

  return (
    <div>
      {/* Image Card */}
      <div
        className="bg-gray-800 text-gray-300 p-6 rounded-lg border-2 border-white cursor-pointer"
        onClick={toggleExpand}
      >
        <img src={imageUrl} alt={title} className="w-full h-48 object-cover rounded-lg mb-4" />
        <h3 className="text-2xl font-bold mb-2">{title}</h3>
      </div>

      {/* Expanded Image Modal */}
      {isExpanded && (
        <div className="fixed inset-0 bg-black bg-opacity-75 flex items-center justify-center z-50">
          <div className="relative p-4 max-w-4xl w-full">
            <button
              className="absolute top-4 right-4 text-white text-2xl focus:outline-none"
              onClick={toggleExpand}
            >
              &times;
            </button>
            <img
              src={imageUrl}
              alt={title}
              className="w-full h-auto rounded-lg shadow-lg"
            />
          </div>
        </div>
      )}
    </div>
  );
}

export default ImageCard;
