import React from 'react';

function ImageGrid({ children, columns }) {
  const columnsClassMap = {
    1: 'grid-cols-1',
    2: 'grid-cols-2',
    3: 'grid-cols-3',
    4: 'grid-cols-4',
    5: 'grid-cols-5',
    6: 'grid-cols-6',
  };

  const columnsClass = columnsClassMap[columns] || 'grid-cols-1'; // Default to 1 column if not specified

  // Check if there's only one image
  const isSingleImage = React.Children.count(children) === 1;

  return (
    <div
      className={`grid ${
        isSingleImage ? 'place-items-center' : columnsClass
      } gap-4 w-full`}
    >
      {React.Children.map(children, (child) => {
        // Apply special styles for the single image
        return React.cloneElement(child, {
          className: isSingleImage
            ? 'w-1/2 aspect-square' // Half width and maintain square aspect ratio
            : '',
        });
      })}
    </div>
  );
}

export default ImageGrid;
