import React, { useState } from 'react';
import { FaSearchPlus } from 'react-icons/fa'; // Import the magnifying glass icon

function ImageCard({ title, imageUrl }) {
  const [isExpanded, setIsExpanded] = useState(false);
  const [isHovered, setIsHovered] = useState(false); // State for hover effect

  // Handle image click to expand or close
  const toggleExpand = () => {
    setIsExpanded(!isExpanded);
  };

  // Handle modal background click (close when clicking outside the image)
  const handleBackgroundClick = (e) => {
    if (e.target.tagName !== 'IMG') {
      setIsExpanded(false);
    }
  };

  return (
    <div>
      {/* Image Card without background */}
      <div
        className="text-gray-300 p-6 rounded-lg cursor-pointer relative"
        onClick={toggleExpand}
        onMouseEnter={() => setIsHovered(true)}
        onMouseLeave={() => setIsHovered(false)}
      >
        {/* Image with hover effect */}
        <img
          src={imageUrl}
          alt={title}
          className={`w-full h-48 object-cover rounded-lg mb-4 border-2 border-white transition-transform duration-300 ${
            isHovered ? 'scale-105 brightness-75' : ''
          }`}
        />

        {/* Magnifying glass icon, shown on hover */}
        {isHovered && (
          <div className="absolute inset-0 flex items-center justify-center">
            <FaSearchPlus className="text-white text-4xl" />
          </div>
        )}

        <h3 className="text-2xl font-bold mb-2 text-center">{title}</h3> {/* Centered caption */}
      </div>

      {/* Expanded Image Modal */}
      {isExpanded && (
        <div
          className="fixed inset-0 bg-black bg-opacity-75 flex items-center justify-center z-50"
          onClick={handleBackgroundClick} // Close modal by clicking outside the image
        >
          <div
            className="relative p-4 max-w-4xl w-full transform transition-transform duration-1000 ease-in-out"
            style={{
              transform: isExpanded ? 'scale(1)' : 'scale(0)', // Smooth expansion
            }}
          >
            <img
              src={imageUrl}
              alt={title}
              className="w-full h-auto rounded-lg shadow-lg border-2 border-white"
              style={{
                transition: 'transform 1s ease-in-out', // Slow and smooth transition for image itself
              }}
            />
          </div>
        </div>
      )}
    </div>
  );
}

export default ImageCard;
