// ImageGrid.js
import React from 'react';

function ImageGrid({ children, columns }) {
  // Map the number of columns to Tailwind CSS grid classes
  const columnsClassMap = {
    1: 'grid-cols-1',
    2: 'grid-cols-2',
    3: 'grid-cols-3',
    4: 'grid-cols-4',
    5: 'grid-cols-5',
    6: 'grid-cols-6',
    // Add more mappings if needed
  };

  const columnsClass = columnsClassMap[columns] || 'grid-cols-1'; // Default to 1 column if not specified

  return (
    <div className={`grid ${columnsClass} gap-4 w-full`}>
      {children}
    </div>
  );
}

export default ImageGrid;
